import React from 'react';
import PropTypes from 'prop-types';
import { Panel, PanelHeader, PanelHeaderBack, Group, Header, Cell, Avatar, Div, Button, CardGrid, Card, Caption } from '@vkontakte/vkui';

// Создаю массив courses с id, названием, описанием и картинкой каждого курса
const courses = [
  {
    id: 'code',
    title: 'Пишем код с нуля',
    description: 'Изучайте основы программирования и создавайте свои собственные приложения',
    image: 'https://cojo.ru/wp-content/uploads/2022/12/belogrudyi-ezh-1-2.webp'
  },
  {
    id: 'design',
    title: 'Рисуем дизайн с нуля',
    description: 'Изучайте основы дизайна и создавайте свои собственные графические проекты',
    image: '../img/persik.png'
  },
  {
    id: 'graphic',
    title: 'Граф дизайн с нуля',
    description: 'Изучайте основы граф дизайна и создавайте свои собственные векторные иллюстрации',
    image: '../img/code-image.png'
  },
  {
    id: 'python',
    title: 'Питон с нуля',
    description: 'Изучайте один из самых популярных и мощных языков программирования',
    image: 'https://cdn.pixabay.com/photo/2017/08/05/11/16/logo-2582748_960_720.png'
  },
  {
    id: 'english',
    title: 'English c нуля',
    description: 'Изучайте английский язык с нуля и повышайте свой уровень владения',
    image: 'https://cdn.pixabay.com/photo/2017/02/18/19/20/logo-2078018_960_720.png'
  }
];

const Courses = ({ id, go }) => (
    <Panel id={id}>
      <PanelHeader
        left={<PanelHeaderBack onClick={go} data-to="home"/>}
      >
        Курсы
      </PanelHeader>
      <Group header={<Header mode="secondary">Выберите курс</Header>}>
        <CardGrid size="l">
          {courses.map(course => (
            <Card mode="shadow" size="l" onClick={go} data-to={course.id} key={course.id}>
              <Div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                <Div style={{ height: 150, borderRadius: 10, overflow: 'hidden' }}>
                  <SimpleCell
                    // Использую компонент Avatar с src равным картинке курса и mode равным "image" в свойстве before
                    before={<Avatar size={288} src={course.image} mode="image" />}
                    // Использую компонент Caption с level равным "2" и weight равным "regular" для отображения описания курса в свойстве description
                    description={<Caption level="2" weight="regular">{course.description}</Caption>}
                    // Использую компонент Caption с level равным "1" и weight равным "regular" для отображения названия курса в свойстве children
                    multiline
                    disabled
                  >
                    <Caption level="1" weight="regular">{course.title}</Caption>
                  </SimpleCell>
                </Div>
              </Div>
            </Card>
          ))}
        </CardGrid>
      </Group>
    </Panel>
  );
  
  Courses.propTypes = {
    id: PropTypes.string.isRequired,
    go: PropTypes.func.isRequired,
  };
  
  export default Courses;
