import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom';
import { AdaptivityProvider, ConfigProvider, AppRoot, SplitLayout, SplitCol, View, Panel, PanelHeader, Header, Group, SimpleCell, Avatar, } from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';
import bridge from '@vkontakte/vk-bridge';

const Home = () => {
  // Хук для хранения данных о пользователе
  const [user, setUser] = useState(null);

  // Функция для получения данных о пользователе с помощью VK Bridge
  const fetchUser = async () => {
    const user = await bridge.send('VKWebAppGetUserInfo');
    setUser(user);
  };

  // Вызываем функцию при монтировании компонента
  useEffect(() => {
    fetchUser();
  }, []);

  return (
    <AppRoot>
      <SplitLayout header={<PanelHeader separator={false} />}>
        <SplitCol autoSpaced>
          <View activePanel="main">
            <Panel id="main">
              <PanelHeader>Образовательное приложение</PanelHeader>
              {user && ( // Если данные о пользователе загружены, то отображаем их
                <Group header={<Header mode="secondary">Профиль</Header>}>
                  <SimpleCell
                    before={<Avatar size={48} src={user.photo_100} />}
                    description={user.city && user.city.title}
                  >
                    Привет, {user.first_name}!
                  </SimpleCell>
                </Group>
              )}
              <Group header={<Header mode="secondary">Меню</Header>}>
                <SimpleCell expandable before={<Icon28EducationOutline />}>
                  Курсы
                </SimpleCell>
                <SimpleCell expandable before={<Icon28VideoOutline />}>
                  Лекции
                </SimpleCell>
                <SimpleCell expandable before={<Icon28BookOutline />}>
                  Глоссарий
                </SimpleCell>
              </Group>
            </Panel>
          </View>
        </SplitCol>
      </SplitLayout>
    </AppRoot>
  );
};

ReactDOM.render(
  <ConfigProvider>
    <AdaptivityProvider>
      <App />
    </AdaptivityProvider>
  </ConfigProvider>,
  document.getElementById('root'),
);
export default Home;
