import React from 'react';
import PropTypes from 'prop-types';
import { Panel, PanelHeader, PanelHeaderBack, Group, Header, CardGrid, Card, Caption, Div, Button, SimpleCell, Avatar } from '@vkontakte/vkui';
import { Icon24Education, Icon56UsersOutline } from '@vkontakte/icons';
import { BrowserRouter, Routes, Route, Link, useParams } from "react-router-dom";

// Добавлен массив уроков для каждого курса с информацией о пройденных уроках
const courses = [
  {
    id: 'code',
    title: 'Пишем код с нуля',
    description: 'Изучайте основы программирования и создавайте свои собственные приложения',
    image: 'https://cojo.ru/wp-content/uploads/2022/12/belogrudyi-ezh-1-2.webp',
    lessons: [
      { title: 'Урок 1', completed: false },
      { title: 'Урок 2', completed: true },
      { title: 'Урок 3', completed: true }
    ]
  },
  // ... Добавьте аналогичные объекты для других курсов
];

// Компонент страницы курса
const CoursePage = () => {
  const { id } = useParams();
  const course = courses.find((c) => c.id === id);
  if (!course) {
    return <div>Курс не найден</div>;
  }

// Функция для генерации блоков с уроками
const renderLessonBlocks = (lessons) => {
    return lessons.map((lesson, index) => (
      <Card style={{ marginBottom: '10px', padding: '10px' }} key={index}>
        <SimpleCell
          before={<Avatar size={48} style={{ background: 'var(--background_content)' }}><Icon24Education /></Avatar>}
          after={
            <Button
              mode="secondary"
              style={{
                border: '1px solid var(--button_secondary_background)',
                color: 'var(--button_secondary_foreground)',
                fontWeight: 'bold',
                fontSize: '1.2em'
              }}
            >
              {lesson.completed ? 'Повторить' : 'Изучить'}
            </Button>
          }
          description={lesson.completed ? 'Урок пройден' : 'Урок не пройден'}
        >
          {lesson.title}
        </SimpleCell>
      </Card>
    ));
  };

  return (
    <div style={{ color: 'white', textAlign: 'center' }}>
      <h1>{course.title}</h1>
      <p>{course.description}</p>
      <div style={{
        backgroundImage: `url(${course.image})`,
        height: 140,
        width: '90%',
        borderRadius: 10,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        margin: '20px auto'
      }} />
      <Div>
        {renderLessonBlocks(course.lessons)}
      </Div>
    </div>
  );
};
  
// Компонент списка курсов
const CoursesList = () => (
  <Group header={<Header mode="secondary">Выберите курс</Header>}>
    <CardGrid size="l">
      {courses.map((course) => (
        <Link to={`/${course.id}`} key={course.id} style={{ textDecoration: 'none', margin: '8px' }}>
          <Card mode="shadow" size="l">
            <Div>
              <Div style={{
                backgroundImage: `url(${course.image})`,
                height: 140,
                width: '90%', // Картинка занимает всю ширину карточки
                borderRadius: 10,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
              }} />
              <Div style={{ 
                display: 'flex', 
                flexDirection: 'column', 
                alignItems: 'center', 
                color: 'white', 
                textAlign: 'center',
                marginTop: 8, 
              }}>
                <Caption level="1" weight="medium" style={{ 
                  marginTop: 16, 
                  fontSize: '1.2em', // Увеличенный размер шрифта
                  fontWeight: 'bold' ,// Жирное начертание
                  marginBottom: '16px',
                }}>
                  {course.title}
                </Caption>
                <Caption level="2" weight="regular">
                    {course.description}
                  </Caption>              
                  </Div>
            </Div>
          </Card>
        </Link>
      ))}
    </CardGrid>
  </Group>
);

  
  
  
  
// Основной компонент Курсы
const Courses = ({ id, go }) => {
    return (
      <Panel id={id}>
        <PanelHeader left={<PanelHeaderBack onClick={() => go('home')} />}>
          Курсы
        </PanelHeader>
        <BrowserRouter>
          <Routes>
            <Route path="/" element={<CoursesList />} />
            <Route path=":id" element={<CoursePage />} />
          </Routes>
        </BrowserRouter>
      </Panel>
    );
  };
  
  Courses.propTypes = {
    id: PropTypes.string.isRequired,
    go: PropTypes.func.isRequired,
  };
  
  export default Courses;
