import React, { useState, useEffect } from 'react';
import bridge from '@vkontakte/vk-bridge';
import { View, ScreenSpinner, AdaptivityProvider, AppRoot, ConfigProvider, SplitLayout, SplitCol, PanelHeader, PanelHeaderButton } from '@vkontakte/vkui';
import Icon28HomeOutline from '@vkontakte/icons/dist/28/home_outline';
import '@vkontakte/vkui/dist/vkui.css';

import Home from './panels/Home';
import Courses from './panels/Courses';
import VideoPage from './panels/Video';

const App = () => {
  const [activePanel, setActivePanel] = useState('home');
  const [fetchedUser, setUser] = useState(null);
  const [popout, setPopout] = useState(<ScreenSpinner size='large' />);

  useEffect(() => {
    async function fetchData() {
      const user = await bridge.send('VKWebAppGetUserInfo');
      setUser(user);
      setPopout(null);
    }
    fetchData();
  }, []);

  const goHome = () => {
    setActivePanel('home');
  };

  const go = (panel) => {
    setActivePanel(panel);
  };

  return (
    <ConfigProvider>
      <AdaptivityProvider>
        <AppRoot>
          <SplitLayout popout={popout}>
            <SplitCol>
              <View activePanel={activePanel}>
                <Home id='home' fetchedUser={fetchedUser} go={() => go('courses')} />
                <Courses id='courses' go={() => go('lectures')}>
                  <PanelHeader left={<PanelHeaderButton onClick={goHome}><Icon28HomeOutline /></PanelHeaderButton>}>
                    Курсы
                  </PanelHeader>
                  {/* Содержимое панели Courses */}
                </Courses>
                <VideoPage id='lectures' go={() => go('home')}>
                  <PanelHeader left={<PanelHeaderButton onClick={goHome}><Icon28HomeOutline /></PanelHeaderButton>}>
                    Лекции
                  </PanelHeader>
                  {/* Содержимое панели VideoPage */}
                </VideoPage>
              </View>
            </SplitCol>
          </SplitLayout>
        </AppRoot>
      </AdaptivityProvider>
    </ConfigProvider>
  );
}

export default App;
