import React from 'react';
import PropTypes from 'prop-types';
import { Panel, PanelHeader, PanelHeaderBack, Group, Header, Cell, Avatar, Div, Button } from '@vkontakte/vkui';

// Array of courses
const courses = [
  {
    id: 'code',
    name: 'Пишем код с нуля',
    description: 'Описание курса по программированию',
    image: 'path/to/code-image.jpg' // replace with actual image paths
  },
  {
    id: 'design',
    name: 'Рисуем дизайн с нуля',
    description: 'Описание курса по дизайну',
    image: 'path/to/design-image.jpg'
  },
  // Add more courses as needed
];

export const Courses = ({ id, go }) => (
  <Panel id={id}>
    <PanelHeader
      left={<PanelHeaderBack onClick={go} data-to="home"/>}
    >
      Курсы
    </PanelHeader>
    <Group header={<Header mode="secondary">Выберите курс</Header>}>
      {courses.map(course => (
        <Cell
          key={course.id}
          before={<Avatar src={course.image} />}
          expandable
          onClick={go}
          data-to={course.id}
        >
          {course.name}
        </Cell>
      ))}
    </Group>
  </Panel>
);

Courses.propTypes = {
  id: PropTypes.string.isRequired,
  go: PropTypes.func.isRequired,
};

export default Courses;
