import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { Panel, PanelHeader, PanelHeaderBack, Group, Header, CardGrid, Card, Caption, Div, ModalRoot, ModalPage, ModalPageHeader, SimpleCell, Button } from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';
// Создаю массив courses с id, названием, описанием и картинкой каждого курса
const courses = [
  {
    id: 'code',
    title: 'Пишем код с нуля',
    description: 'Изучайте основы программирования и создавайте свои собственные приложения',
    image: 'https://cojo.ru/wp-content/uploads/2022/12/belogrudyi-ezh-1-2.webp'
  },
  {
    id: 'design',
    title: 'Рисуем дизайн с нуля',
    description: 'Изучайте основы дизайна и создавайте свои собственные графические проекты',
    image: 'https://cojo.ru/wp-content/uploads/2022/12/belogrudyi-ezh-1-2.webp'
  },
  {
    id: 'graphic',
    title: 'Граф дизайн с нуля',
    description: 'Изучайте основы граф дизайна и создавайте свои собственные векторные иллюстрации',
    image: 'https://cojo.ru/wp-content/uploads/2022/12/belogrudyi-ezh-1-2.webp'
  },
  {
    id: 'python',
    title: 'Питон с нуля',
    description: 'Изучайте один из самых популярных и мощных языков программирования',
    image: 'https://cojo.ru/wp-content/uploads/2022/12/belogrudyi-ezh-1-2.webp'
  },
  {
    id: 'english',
    title: 'English c нуля',
    description: 'Изучайте английский язык с нуля и повышайте свой уровень владения',
    image: 'https://cojo.ru/wp-content/uploads/2022/12/belogrudyi-ezh-1-2.webp'
  }
];

const CoursesModal = ({ course, onClose }) => (
  <ModalPage
    id="course-modal"
    onClose={onClose}
    header={<ModalPageHeader>{course.title}</ModalPageHeader>}
  >
    <Group>
      <SimpleCell
        before={<Avatar src="path/to/video-icon.jpg" />}
        description="Видео лекция"
        after={<Button>Изучить</Button>}
      >
        Урок 1
      </SimpleCell>
      <SimpleCell
        before={<Avatar src="path/to/audio-icon.jpg" />}
        description="Аудиолекция"
        after={<Button>Изучить</Button>}
      >
        Урок 2
      </SimpleCell>
      <SimpleCell
        before={<Avatar src="path/to/document-icon.jpg" />}
        description="Конспект"
        after={<Button>Изучить</Button>}
      >
        Урок 3
      </SimpleCell>
    </Group>
  </ModalPage>
);

const Courses = ({ id, go }) => {
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  const openModal = course => {
    setSelectedCourse(course);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedCourse(null);
  };

  return (
    <Panel id={id}>
      <PanelHeader left={<PanelHeaderBack onClick={go} data-to="home" />}>
        Курсы
      </PanelHeader>
      <Group header={<Header mode="secondary">Выберите курс</Header>}>
        <CardGrid size="l">
          {courses.map(course => (
            <Card mode="shadow" size="l" onClick={go} data-to={course.id} key={course.id}>
            <Div >
              <Div style={{ backgroundImage : `url(${course.image})`, height: 140, width: '90%', borderRadius: 10, backgroundSize: 'cover', backgroundPosition: 'center' }} />
              <Div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                <Caption level="1" weight="regular" style={{ marginTop: 16 }}>
                  {course.title}
                </Caption>
                <Caption level="2" weight="regular" style={{ marginTop: 8 }}>
                  {course.description}
                </Caption>
              </Div>
            </Div>
          </Card>
          ))}
        </CardGrid>
      </Group>
      <ModalRoot activeModal={isModalOpen ? 'course-modal' : null}>
        {selectedCourse && <CoursesModal course={selectedCourse} onClose={closeModal} />}
      </ModalRoot>
    </Panel>
  );
};

Courses.propTypes = {
  id: PropTypes.string.isRequired,
  go: PropTypes.func.isRequired,
};

export default Courses;
