import React from 'react';
import { Panel, PanelHeader, Group, Div } from '@vkontakte/vkui';
import ReactPlayer from 'react-player';

// Данные видеороликов
const videos = [
  {
    title: 'Видео 1',
    description: 'Описание видео 1',
    url: 'https://www.youtube.com/watch?v=example1'
  },
  // ... Другие видеоролики
];

const VideoPage = () => {
  // Состояние для текущего индекса видео
  const [currentVideoIndex, setCurrentVideoIndex] = React.useState(0);

  // Функции для навигации по видео
  const goToNextVideo = () => {
    setCurrentVideoIndex((prevIndex) =>
      prevIndex < videos.length - 1 ? prevIndex + 1 : prevIndex
    );
  };

  const goToPreviousVideo = () => {
    setCurrentVideoIndex((prevIndex) => (prevIndex > 0 ? prevIndex - 1 : prevIndex));
  };

  return (
    <Panel>
      <PanelHeader>Видеоролики</PanelHeader>
      <Group>
        <Div style={{ textAlign: 'center' }}>
          <h2>{videos[currentVideoIndex].title}</h2>
          <ReactPlayer url={videos[currentVideoIndex].url} controls />
          <p>{videos[currentVideoIndex].description}</p>
          <button onClick={goToPreviousVideo}>Предыдущее видео</button>
          <button onClick={goToNextVideo}>Следующее видео</button>
        </Div>
      </Group>
    </Panel>
  );
};

export default VideoPage;
