import React from 'react';
import PropTypes from 'prop-types';
import { Panel, PanelHeader, PanelHeaderBack, Group, Header, CardGrid, Card, Caption, Div } from '@vkontakte/vkui';
import { BrowserRouter, Routes, Route, Link, useParams } from "react-router-dom";
// Создаю массив courses с id, названием, описанием и картинкой каждого курса
const courses = [
  {
    id: 'code',
    title: 'Пишем код с нуля',
    description: 'Изучайте основы программирования и создавайте свои собственные приложения',
    image: 'https://cojo.ru/wp-content/uploads/2022/12/belogrudyi-ezh-1-2.webp'
  },
  {
    id: 'design',
    title: 'Рисуем дизайн с нуля',
    description: 'Изучайте основы дизайна и создавайте свои собственные графические проекты',
    image: 'https://cojo.ru/wp-content/uploads/2022/12/belogrudyi-ezh-1-2.webp'
  },
  {
    id: 'graphic',
    title: 'Граф дизайн с нуля',
    description: 'Изучайте основы граф дизайна и создавайте свои собственные векторные иллюстрации',
    image: 'https://cojo.ru/wp-content/uploads/2022/12/belogrudyi-ezh-1-2.webp'
  },
  {
    id: 'python',
    title: 'Питон с нуля',
    description: 'Изучайте один из самых популярных и мощных языков программирования',
    image: 'https://cojo.ru/wp-content/uploads/2022/12/belogrudyi-ezh-1-2.webp'
  },
  {
    id: 'english',
    title: 'English c нуля',
    description: 'Изучайте английский язык с нуля и повышайте свой уровень владения',
    image: 'https://cojo.ru/wp-content/uploads/2022/12/belogrudyi-ezh-1-2.webp'
  }
];

// Компонент страницы курса
const CoursePage = () => {
    const { id } = useParams();
    const course = courses.find((c) => c.id === id);
    if (!course) {
      return <div>Курс не найден</div>;
    }
    return (
      <div>
        <h1>{course.title}</h1>
        <p>{course.description}</p>
        <img src={course.image} alt={course.title} />
      </div>
    );
  };
  
  // Компонент списка курсов
// Компонент списка курсов
const CoursesList = () => (
    <Group header={<Header mode="secondary">Выберите курс</Header>}>
      <CardGrid size="l">
        {courses.map((course) => (
          <Link to={`/${course.id}`} key={course.id} style={{ textDecoration: 'none' }}>
            <Card mode="shadow" size="l">
              <Div>
                <Div style={{
                  backgroundImage: `url(${course.image})`,
                  height: 140,
                  width: '90%',
                  borderRadius: 10,
                  backgroundSize: 'cover',
                  backgroundPosition: 'center',
                  margin: '0 auto' // Добавлено для центрирования изображения
                }} />
                <Div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', color: 'black' }}> {/* Добавлен цвет текста, чтобы он был виден на фоне карточки */}
                  <Caption level="1" weight="regular" style={{ marginTop: 16 }}>
                    {course.title}
                  </Caption>
                  <Caption level="2" weight="regular" style={{ marginTop: 8 }}>
                    {course.description}
                  </Caption>
                </Div>
              </Div>
            </Card>
          </Link>
        ))}
      </CardGrid>
    </Group>
  );
  
  
  // Основной компонент Курсы
  const Courses = ({ id, go }) => {
    return (
      <Panel id={id}>
        <PanelHeader left={<PanelHeaderBack onClick={() => go('home')} />}>
          Курсы
        </PanelHeader>
        <BrowserRouter>
          <Routes>
            <Route path="/" element={<CoursesList />} />
            <Route path=":id" element={<CoursePage />} />
          </Routes>
        </BrowserRouter>
      </Panel>
    );
  };
  
  Courses.propTypes = {
    id: PropTypes.string.isRequired,
    go: PropTypes.func.isRequired,
  };
  
  export default Courses;
