import React from 'react';
import { useState } from 'react';
import PropTypes from 'prop-types';
import { Button, Modal, ModalPage, ModalPageHeader, PanelHeaderButton, Textarea, SelectMimicry, Panel,List,Cell, PanelHeader, PanelHeaderBack, Group, Header, CardGrid, Card, Caption, Div, SimpleCell, Avatar } from '@vkontakte/vkui';
import { Icon24Education, Icon56UsersOutline } from '@vkontakte/icons';
import { BrowserRouter, Routes, Route, Link, useParams, useNavigate } from "react-router-dom";
import ReactPlayer from 'react-player';
import { Icon24Cancel } from '@vkontakte/icons';

// Обновленные данные курсов с информацией о видео и описании уроков
const courses = [
  {
    id: 'code',
    title: 'Пишем код с нуля',
    description: 'Изучайте основы программирования и создавайте свои собственные приложения',
    image: 'https://cojo.ru/wp-content/uploads/2022/12/belogrudyi-ezh-1-2.webp',
    lessons: [
      {
        title: 'Урок 1',
        completed: false,
        videoUrl: '0ly25OYC45M',
        description: 'Описание урока 1'
      },
      // ... Другие уроки
    ]
  },
  // ... Другие курсы
];

// Компонент страницы урока
const LessonPage = () => {
  const { courseId, lessonId } = useParams();
  const course = courses.find(c => c.id === courseId);
  const lesson = course?.lessons[parseInt(lessonId, 10) - 1];
  const [glossaryOpen, setGlossaryOpen] = useState(false); // Состояние для открытия/закрытия глоссария
  const [isModalOpen, setIsModalOpen] = useState(false); // Состояние для управления модальным окном
  const [notes, setNotes] = useState(''); // Состояние для заметок

  // Функция для открытия модального окна
  const openModal = () => {
    setIsModalOpen(true);
  };

  // Функция для закрытия модального окна
  const closeModal = () => {
    setIsModalOpen(false);
  };
  // Функция для переключения видимости глоссария
  const toggleGlossary = () => {
    setGlossaryOpen(!glossaryOpen);
  };
  if (!lesson) {
    return <div>Урок не найден</div>;
  }
  return (
    <div style={{ textAlign: 'center' }}>
      <h1>{lesson.title}</h1>
      <p>{lesson.description}</p>
      <div style={{ maxWidth: '80%', margin: 'auto' }}>
        <ReactPlayer 
          url={`https://www.youtube.com/watch?v=${lesson.videoUrl}`} 
          controls 
          width='100%' 
          height='100%'
          style={{ borderRadius: '20px' }} 
        />
      </div>
      <div style={{ marginTop: '20px' }}>
      <Button size="l" mode="primary" onClick={openModal}>Открыть лекцию</Button> {/* Кнопка для открытия модального окна */}

      {isModalOpen && (
        <Modal onClose={closeModal}>
          <ModalPage
            header={
              <ModalPageHeader
                left={<PanelHeaderButton onClick={closeModal}><Icon24Cancel /></PanelHeaderButton>}
              >
                Текст лекции
              </ModalPageHeader>
            }
          >
            {/* Здесь можно добавить текст лекции */}
            <Textarea 
              top="Заметки" 
              placeholder="Введите ваши заметки здесь..." 
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              style={{ height: '200px' }}
            />
          </ModalPage>
        </Modal>
      )}
        </div>
      <div style={{ marginTop: '10px' }}>
        <SelectMimicry top="Глосарий" onClick={toggleGlossary} placeholder="Выберите термин"> {/* Выпадающий список */}
          {glossaryOpen && (
            <List>
              {/* Здесь можно добавить элементы списка с терминами и описаниями */}
              <Cell>Термин 1</Cell>
              <Cell>Термин 2</Cell>
              {/* и так далее */}
            </List>
          )}
        </SelectMimicry>
      </div>
    </div>
  );
};


// Компонент страницы курса
const CoursePage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const course = courses.find(c => c.id === id);

  if (!course) {
    return <div>Курс не найден</div>;
  }

  const renderLessonBlocks = (lessons) => {
    return lessons.map((lesson, index) => (
      <Card style={{ marginBottom: '10px', padding: '10px' }} key={index}>
        <SimpleCell
          before={<Avatar size={48} style={{ background: 'var(--background_content)' }}><Icon24Education /></Avatar>}
          after={
            <Button
              mode={lesson.completed ? 'secondary' : 'primary'}
              size="l"
              onClick={() => navigate(`/${id}/lesson/${index + 1}`)}
            >
              {lesson.completed ? 'Повторить' : 'Изучить'}
            </Button>
          }
        >
          {lesson.title}
          <div style={{ fontSize: '12px', color: 'var(--text_secondary)' }}>
            {getLessonType(index)}
          </div>
        </SimpleCell>
      </Card>
    ));
  };

  const getLessonType = (index) => {
    switch (index) {
      case 0:
        return 'Видеолекция';
      case 1:
        return 'Аудио лекция';
      case 2:
        return 'Конспект';
      default:
        return '';
    }
  };

  return (
    <div style={{ color: 'white', textAlign: 'center' }}>
      <h1>{course.title}</h1>
      <p>{course.description}</p>
      <div style={{
        backgroundImage: `url(${course.image})`,
        height: 140,
        width: '90%',
        borderRadius: 10,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
        margin: '20px auto'
      }} />
      <Div>
        {renderLessonBlocks(course.lessons)}
      </Div>
    </div>
  );
};
  
// Компонент списка курсов
const CoursesList = () => (
  <Group header={<Header mode="secondary">Выберите курс</Header>}>
    <CardGrid size="l">
      {courses.map((course) => (
        <Link to={`/${course.id}`} key={course.id} style={{ textDecoration: 'none', margin: '8px' }}>
          <Card mode="shadow" size="l">
            <Div>
              <Div style={{
                backgroundImage: `url(${course.image})`,
                height: 140,
                width: '90%', // Картинка занимает всю ширину карточки
                borderRadius: 10,
                backgroundSize: 'cover',
                backgroundPosition: 'center',
              }} />
              <Div style={{ 
                display: 'flex', 
                flexDirection: 'column', 
                alignItems: 'center', 
                color: 'white', 
                textAlign: 'center',
                marginTop: 8, 
              }}>
                <Caption level="1" weight="medium" style={{ 
                  marginTop: 16, 
                  fontSize: '1.2em', // Увеличенный размер шрифта
                  fontWeight: 'bold' ,// Жирное начертание
                  marginBottom: '16px',
                }}>
                  {course.title}
                </Caption>
                <Caption level="2" weight="regular">
                    {course.description}
                  </Caption>              
                  </Div>
            </Div>
          </Card>
        </Link>
      ))}
    </CardGrid>
  </Group>
);

  
  
  
  
// Основной компонент Курсы с роутингом
const Courses = ({ id, go }) => {
    return (
      <Panel id={id}>
        <PanelHeader left={<PanelHeaderBack onClick={() => go('home')} />}>
          Курсы
        </PanelHeader>
        <BrowserRouter>
          <Routes>
            <Route path="/" element={<CoursesList />} />
            <Route path="/:id" element={<CoursePage />} />
            <Route path="/:courseId/lesson/:lessonId" element={<LessonPage />} />
          </Routes>
        </BrowserRouter>
      </Panel>
    );
  };
  
  Courses.propTypes = {
    id: PropTypes.string.isRequired,
    go: PropTypes.func.isRequired,
  };
  
  export default Courses;
