import React from 'react';
import { Panel, PanelHeader, Group, List, Cell } from '@vkontakte/vkui';

const GlossaryPage = () => {
  // Данные глоссария
  const glossaryTerms = [
    { term: 'Термин 1', description: 'Описание термина 1' },
    { term: 'Термин 2', description: 'Описание термина 2' },
    // ... Другие термины
  ];

  return (
    <Panel>
      <PanelHeader>Глоссарий</PanelHeader>
      <Group title="Термины">
        <List>
          {glossaryTerms.map((item, index) => (
            <Cell key={index} description={item.description}>
              {item.term}
            </Cell>
          ))}
        </List>
      </Group>
    </Panel>
  );
};

export default GlossaryPage;
