import React from 'react';
import { Panel, PanelHeader, Group, Cell, Avatar, Header, Div } from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';
import Icon28AddOutline from '@vkontakte/icons/dist/28/add_outline';

const ProfilePage = ({ id, user }) => {
  return (
    <Panel id={id}>
      <PanelHeader>Профиль</PanelHeader>
      <Group>
        <Div>
          <Avatar src={user.photo_200} size={96} />
          <Header mode="secondary">Привет, {user.first_name}!</Header>
        </Div>

        <Cell before={<Icon28AddOutline />} expandable>
          Курсы
        </Cell>
        <Cell before={<Icon28AddOutline />} expandable>
          Лекции
        </Cell>
        <Cell before={<Icon28AddOutline />} expandable>
          Глоссарий
        </Cell>
      </Group>
    </Panel>
  );
};

export default ProfilePage;
